import {
    ActivityIndicator,
    Button, FlatList,
    Image,
    SafeAreaView,
    ScrollView,
    Text,
    TextInput,
    TouchableHighlight,
    useColorScheme,
    useWindowDimensions,
    View
} from "react-native";
import {Component, useEffect, useRef, useState} from "react";
import * as React from "react";
import {styles} from "../css";
import {Portrait} from "../component/Portrait";
import {Btn} from "../component/btn";
import {useFocusEffect} from "@react-navigation/native";
import {_Column, _Msg, _Unread} from "../_Api";
import {io} from "socket.io-client";
import {OssImage, pickImage} from "../utils/oss";
import * as ImagePicker from "expo-image-picker";
import {Record} from "../utils/record";
import {Audio} from 'expo-av';
import {timeIm} from "../utils/time";
import KeyboardAvoidingView from "react-native/Libraries/Components/Keyboard/KeyboardAvoidingView";

const socket = io('ws://192.168.0.101:3000',)

export function Im({route, navigation}) {
    const colorScheme = useColorScheme();

    // Ë∑ØÁî±ÂèÇÊï∞
    const {list, user, name, to,unread} = route.params;

    const C1 = colorScheme == 'light' ? styles.lightC1 : styles.darkC1
    const MsgColor = colorScheme == 'light' ? styles.lightMsg : styles.darkMsg  //ÂØπËØùÊ°ÜÈÖçËâ≤
    const MstText = colorScheme == 'light' ? {color: '#222222'} : {color: '#ffffff'}    //ÂØπËØùÊ°ÜÈ¢úËâ≤

    const [input, setInput] = useState('')  //ËæìÂÖ•‰ø°ÊÅØ
    const [audioLoad, setAudioLoad] = useState()  //ÂΩïÂà∂Â£∞Èü≥
    const [sounds, setSound] = useState();   // Êí≠ÊîæÂ£∞Èü≥
    const [seconds, setSeconds] = useState(1)   //ËÆ°Êó∂Êó∂Èïø
    const [imi,setImi] = useState(null)

    const [columnLi,setColumnLi] = useState([]) //ËØçÂàó
    const columnRef = useRef(columnLi)
    columnRef.current = columnLi

    const _ref = useRef(null)           //ScrollView ÊéßÂà∂Âô®
    const [msgArr, setMsgArr] = useState([])        //ÂØπËØùÂàóË°®
    const msgRef = useRef(msgArr)
    msgRef.current = msgArr


    const _interval = useRef()  //ËÆ°Êó∂Âô®

    const window = useWindowDimensions();

    //Êí≠ÊîæÂ£∞Èü≥
    const [soundIndex,setSoundIndex] = useState(null)  //Êí≠ÊîæÂ£∞Èü≥‰∏ãÊ†á
    async function onPlaySound(url) {
        console.log('Loading Sound');
        const {sound} = await Audio.Sound.createAsync({uri: url});
        setSound(sound);
        console.log('Playing Sound');
        await sound.playAsync();
    }

    useEffect(() => {
        return sounds
            ? () => {
                console.log('Unloading Sound');
                sounds.unloadAsync();
            }
            : undefined;
    }, [sounds]);

    // Ë∑ØÁî±ÁîüÂëΩÂë®Êúü
    useFocusEffect(React.useCallback(() => {
        console.log(`ËøõÂÖ•imÔºå‰ø°ÈÅì: ${list}, Áî®Êà∑${user}`)
        navigation.setOptions({title: name})


        // Êé•Êî∂‰ø°ÊÅØ
        socket.on(list, (im) => {
            let arr = msgRef.current
            arr.push(im)
            // console.log('im:',arr.length,im)
            setMsgArr([...arr])
            setTimeout(()=>_ref.current.scrollToEnd({animated:true}),100)

        });

        // Âä†ËΩΩÂØπËØùÂÜÖÂÆπ
        _Msg(list, cd => {
            console.log('ÊªöÂä®ÂØπËØù„ÄÅ„ÄÅ', cd.length)
            setMsgArr(cd)
            cd.length? setTimeout(()=>_ref.current.scrollToEnd({animated:true}),100):''

        })

        return () => {

            // Êõ¥Êñ∞Êó∂Èó¥Êà≥
            let msgSlice = msgRef.current.length?msgRef.current[msgRef.current.length-1]:{tIm:4}
            console.log('Á¶ªÂºÄim,Êñ≠ÂºÄÈìæÊé•',msgSlice.tIm)
            if(msgSlice.tIm != 4){
                let time = new Date()
                let imData = {
                    "user": user,
                    "list": list,
                    "to": to,
                    "q": time,
                    "tIm": 4
                }
                socket.emit('im', imData)
                console.log('Êõ¥Êñ∞Êó∂Èó¥Êà≥')
            }
            socket.off(list)
            if(unread){
                _Unread(list,user)
            }

