import {
    Alert,
    FlatList,
    Text,
    TextInput,
    TouchableHighlight,
    useColorScheme,
    useWindowDimensions,
    View
} from "react-native";
import {useCallback, useEffect, useRef, useState} from "react";
import {styles} from "../css";
import {Portrait} from "../component/Portrait";
import * as React from "react";
import {useFocusEffect} from '@react-navigation/native';
import {_name, _storeDel, _storeLi, _User} from "../_Api";
import {Btn, TextClick} from "../component/btn";
import * as ImagePicker from "expo-image-picker";
import {upAvatar} from "../utils/oss";

export function Me({navigation}) {
    const colorScheme = useColorScheme();
    const C1 = colorScheme == 'light' ? styles.lightC1 : styles.darkC1
    const C2 = colorScheme == 'light' ? styles.lightC2 : styles.darkC2

    const MsgColor = colorScheme == 'light' ? styles.lightMsg : styles.darkMsg  //对话框配色
    const MstText = colorScheme == 'light' ? {color: '#222222'} : {color: '#ffffff'}    //对话框颜色
    const MeBbc = colorScheme == 'light' ? styles.lightNsgBcB : styles.darkNsgBcB    //对话框颜色
    const MsgColorTouchable = colorScheme == 'light' ? '#EAEAEA' : '#333C52'    //点击对话框

    const [load, setLoad] = useState(false)
    const [user, setUser] = useState({})
    const userRef = useRef(user)
    userRef.current = user

    const [name, setName] = useState('')    //昵称
    const [upName, setUpName] = useState(false)    //修改昵称
    const [jMa, setJma] = useState(false)    //激活码
    const [store, setStore] = useState([])       //收藏列表
    const [t_value, setT_value] = useState('')  //搜索内容

    navigation.addListener('focus', () => {
        console.log('更新用户信息')
        _User(cb => {
            setUser(cb)
            _storeLi(cb => {
                console.log('收藏列表', cb)
                setStore(cb)
            })
        })
        navigation.setOptions({
            title: "我的",
            headerRight: () => <Text style={[styles.T4, C2, styles.bold,]} onPress={() => Alert.alert('退出登陆', '', [
                {
                    text: '换号',
                    onPress: () => navigation.navigate('Login')
                },
                {
                    text: '退出',
                    onPress: () => {
                    }
                },
                {
                    text: '取消',
                    onPress: () => {
                    }
                },
            ])}>退出</Text>
        })
        return () => {
            console.log('离开我的',)
        };
    })


    //搜索text内容
    function onStore(text) {
        setT_value(text)
        let _Text = text
        const debounceTask = debounce(task, 1000)
    }

    return <View style={[styles.Me, C1]}>
        {/*个人信息*/}

        <View style={styles.MeUse}>
            <TouchableHighlight
                underlayColor={MsgColorTouchable}
                onPress={() => Alert.alert('替换头像', '', [
                    {
                        text: '换头像',
                        onPress: () => upAvatar(user.id, cb => {
                            setUser(cb)
                        })
                    },
                    {
                        text: '取消',
                        onPress: () => {
                        }
                    },
                ])}
            >
                <Portrait w={52} h={52} r={50} t={user.emoji} url={user.avatar}/>
            </TouchableHighlight>

            <View style={styles.MeUseText}>
                {upName ? <View style={styles.upName}>
                    <TextInput style={[styles.upNameInput, {backgroundColor: MsgColorTouchable}]}
                               value={name}
                               onChangeText={(text) => setName(text)}/>
                    <Btn text={'👌'} fs={18} press={() => Alert.alert('昵称变动', '确认修改你的昵称', [
                        {
                            text: '确定',
                            onPress: () => {
                                _name(name, newUser => setUser(newUser))
                                setUpName(false)
                            }
                        },
                        {
                            text: '取消',
                            onPress: () => console.log(111)
                        }
                    ])}/>
                </View> : <TouchableHighlight
                    underlayColor={MsgColorTouchable}
                    onPress={() => Alert.alert('修改昵称', '', [
                        {
                            text: '修改',
                            onPress: () => setUpName(true)
                        },
                        {
                            text: '取消',
                            onPress: () => {
                            }
                        },
                    ])}>
                    <Text style={[styles.T3, styles.bold, C2]}>{user.name} </Text>
                </TouchableHighlight>}
                <Text style={[styles.T5, C2, styles.bold, {opacity: 0.9, marginTop: 6}]}>🆔 {user.id} </Text>
            </View>
            {1 ? <Text style={[styles.T6, styles.bold, styles.vip]} onPress={() => Alert.alert('开通功能',
                    '【词裂】提高词汇与句法，【跟读】加强听力口语', [
                        {
                            text: '使用激活码',
                            onPress: () => setJma(true)
                        },
                        {
                            text: '取消',
                            onPress: () => {
                            }
                        },
                    ])}>开通功能</Text> :
                <Text style={[styles.T5, styles.bold, styles.vip, {borderWidth: 0}]}
                      onPress={() => Alert.alert('会员信息',
                          '【词裂】提高词汇与句法，【跟读】加强听力口语', [
                              {
                                  text: '充值记录',
                                  onPress: () => {
                                  }
                              },
                              {
                                  text: '续费',
                                  onPress: () => {
                                  }
                              },
                              {
                                  text: '取消',
                                  onPress: () => {
                                  }
                              },
                          ])}>T-300</Text>}


        </View>

        {/*使用激活码*/}
        {jMa ? <View style={styles.jMa}>
            <Text style={[styles.T5, MstText]}> 使用激活码</Text>
            <View style={styles.jMaSend}>
                <TextInput style={[{backgroundColor: MsgColorTouchable}, styles.jMaInput]}/>

                <Text style={styles.jMaBtn} onPress={() => {
                    Alert.alert('激活成功', '获得30天的使用期', [
                        {
                            text: 'ok',
                            onPress: () => setJma(false)
                        }
                    ])
                }}>激活</Text>
            </View>
        </View> : ''}

        {/*搜索收藏*/}
        <View style={[styles.MeInput, MeBbc]}>
            <Text style={[MstText, styles.T5, styles.bold]}>🔍 收藏夹</Text>
            <TextInput value={t_value} style={[styles.MeInputs, styles.T6, MstText]}
                       onChangeText={(text) => onStore(text)}/>
        </View>


        <FlatList
            data={store}
            refreshing={load}
            onRefresh={() => {
                console.log('下啦刷新')
            }}
            renderItem={({item, i}) => <View style={[styles.ImMsg, {marginLeft: 15}]}>
                <Portrait w={32} h={32} r={7} url={item.user.avatar} t={item.user.emoji}/>
                <LeftMsg im={item} storeUp={() => _storeLi(cb => {
                    console.log('收藏列表', cb)
                    setStore(cb)
                })}/>
            </View>}
        />
    </View>
}


function LeftMsg(props) {
    const {im, storeUp} = props
    const colorScheme = useColorScheme();
    const MsgColor = colorScheme == 'light' ? styles.lightMsg : styles.darkMsg  //对话框配色
    const MsgColorTouchable = colorScheme == 'light' ? '#EAEAEA' : '#333C52'    //点击对话框
    const MstText = colorScheme == 'light' ? {color: '#222222'} : {color: '#ffffff'}    //对话框颜色
    const lightNsgBcB = colorScheme == 'light' ? styles.lightNsgBcB : styles.darkNsgBcB    //对话框分割线
    const window = useWindowDimensions();
    switch (im.tIm) {
        case 1:
            return <View style={[styles.msgRow]}>
                <View style={[styles.msgText, {maxWidth: (0.6 * window.width)}, MsgColor]}>
                    <Text style={[styles.T5, styles.zh, MstText, lightNsgBcB]}> {im.q}</Text>
                    <TextClick click={() => console.log('点击')}
                               doubleClick={() => console.log('双击')}
                               press={() => Alert.alert('删除收藏', '确认删除后，收藏将不可恢复', [
                                   {
                                       text: "确认",
                                       onPress: () => {
                                           _storeDel(im._id, cb => {
                                               console.log('移除成功')
                                               storeUp()
                                           })
                                       }
                                   },
                                   {
                                       text: "取消",
                                       onPress: () => {
                                       }
                                   },
                               ])}
                               slot={<Text style={[styles.T6, MstText, styles.en, {opacity: 0.8}]}> {im.enQ} </Text>}/>
                </View>
                <TouchableHighlight activeOpacity={0.3} underlayColor={MsgColorTouchable}>
                    <Text style={[styles.msgAudio, styles.T5]}> 🎵</Text>
                </TouchableHighlight>
            </View>
            break;
        case 2:
            return <View style={[styles.msgRow]}>
                <TouchableHighlight style={[styles.msgText, MsgColor]} activeOpacity={0.3}
                                    onPress={() => {
                                    }}
                                    underlayColor={MsgColorTouchable}>
                    <Text style={[styles.T5, styles.en, MstText]}> 34 "</Text>
                </TouchableHighlight>
            </View>
            break;
        case 3:
            return <View style={[styles.msgRow]}>
                <TouchableHighlight activeOpacity={0.5}
                                    underlayColor={MsgColorTouchable}
                                    onPress={() => {
                                    }}>
                    <MsgImg/>
                </TouchableHighlight>
            </View>
            break;
    }


}
