import {
    ActivityIndicator,
    Alert,
    FlatList, Image, Modal,
    Text,
    TextInput,
    TouchableHighlight, TouchableOpacity,
    useColorScheme,
    useWindowDimensions,
    View
} from "react-native";
import {useCallback, useEffect, useRef, useState} from "react";
import {styles} from "../css";
import {Portrait} from "../component/portrait";
import * as React from "react";
import {useFocusEffect} from '@react-navigation/native';
import {_addStore, _name, _storeDel, _storeLi, _storeQuery, _User} from "../_Api";
import {Btn, TextClick} from "../component/btn";
import * as ImagePicker from "expo-image-picker";
import {upAvatar} from "../utils/oss";
import {Audio} from "expo-av";
import {memberFun} from "../utils/time";
import * as Clipboard from "expo-clipboard";
import AsyncStorage from "@react-native-async-storage/async-storage";
const host = 'http://192.168.0.100:3000/audio/'



export function Me({navigation}) {
    const colorScheme = useColorScheme();
    const C1 = colorScheme == 'light' ? styles.lightC1 : styles.darkC1
    const C2 = colorScheme == 'light' ? styles.lightC2 : styles.darkC2
    const BbC = colorScheme == 'light' ? styles.lightBbC : styles.darkBbC
    const MsgColor = colorScheme == 'light' ? styles.lightMsg : styles.darkMsg  //ÂØπËØùÊ°ÜÈÖçËâ≤
    const MstText = colorScheme == 'light' ? {color: '#222222'} : {color: '#ffffff'}    //ÂØπËØùÊ°ÜÈ¢úËâ≤
    const MeBbc = colorScheme == 'light' ? styles.lightNsgBcB : styles.darkNsgBcB    //ÂØπËØùÊ°ÜÈ¢úËâ≤
    const MsgColorTouchable = colorScheme == 'light' ? '#EAEAEA' : '#333C52'    //ÁÇπÂáªÂØπËØùÊ°Ü
    const placeholderColor =  colorScheme == 'light' ? '#222222' :'#ffffff'    //ËæìÂÖ•Ê°Ü

    const [load, setLoad] = useState(false)
    const [user, setUser] = useState({})
    const userRef = useRef(user)
    userRef.current = user

    const [name, setName] = useState('')    //ÊòµÁß∞
    const [upName, setUpName] = useState(false)    //‰øÆÊîπÊòµÁß∞
    const [store, setStore] = useState([])       //Êî∂ËóèÂàóË°®
    const [on,setOn] = useState(NaN)
    const [word, setWord] = useState([])     //ËØçÂÖ∏

    navigation.addListener('focus', () => {
        console.log('Êõ¥Êñ∞Áî®Êà∑‰ø°ÊÅØ')
        _User(cb => {
            setUser(cb)
            _storeLi(cb => {
                console.log('Êî∂ËóèÂàóË°®', cb)
                setStore(cb)
            })
        })
        navigation.setOptions({
            title: "ÊàëÁöÑ",
            headerRight: () => <Text style={[styles.T4, C2, styles.bold,]} onPress={() => Alert.alert('ÈÄÄÂá∫ÁôªÈôÜ', '', [
                {
                    text: 'Á°ÆÂÆö',
                    onPress: () => {
                        AsyncStorage.removeItem('token')
                        AsyncStorage.removeItem('tokenIn')
                        navigation.navigate('Login')
                    }
                },
                {
                    text: 'ÂèñÊ∂à',
                    onPress: () => {
                    }
                },
            ])}>ÁôªÂá∫</Text>
        })
        return () => {
            console.log('Á¶ªÂºÄÊàëÁöÑ',)
        };
    })


    //ÊêúÁ¥¢textÂÜÖÂÆπ
    function onStore(text) {
        setStore([])
        let arr = store
        _storeQuery(text, cb => {
            console.log('ÊêúÁ¥¢Êî∂Ëóè', cb)
            if(cb.length){
                setStore(cb)
            }else {
                Alert.alert('ÊêúÁ¥¢ÁªìÊûú', 'Ê≤°Êúâ‰Ω†ÊÉ≥Ë¶ÅÁöÑÁªìÊûú„ÄÅ',[
                    {
                        text:'ok',
                        onPress:()=> setStore(arr)
                    }
                ])
            }

        })
    }

    //Êí≠ÊîæÂ£∞Èü≥
    const [sound, setSound] = React.useState();
    const playSound = async ( i,url) => {
        console.log('Êí≠ÊîæÂ£∞Èü≥',i,url)
        if (url != 'null') {
            setOn(i)
            const {sound} = await Audio.Sound.createAsync({uri: url});
            setSound(sound);
            console.log('Playing Sound');
            await sound.playAsync();
        } else {
            setOn(NaN)
            sound.unloadAsync();
        }
    }
    useEffect(() => {
        return sound
            ? () => {
                console.log('Unloading Sound');
                sound.unloadAsync();
            }
            : undefined;
    }, [sound])


    return <View style={[styles.Me, C1]}>
        {/*‰∏™‰∫∫‰ø°ÊÅØ*/}
        <View style={styles.MeUse}>
            <TouchableHighlight
                underlayColor={MsgColorTouchable}
                onPress={() => Alert.alert('ÊõøÊç¢Â§¥ÂÉè', '', [
                    {
                        text: 'Êç¢Â§¥ÂÉè',
                        onPress: () => upAvatar(user.id, cb => {
                            setUser(cb)
                        })
                    },
                    {
                        text: 'ÂèñÊ∂à',
                        onPress: () => {
                        }
                    },
                ])}
            >
                <Portrait w={52} h={52} r={50} t={user.emoji} url={user.avatar}/>
