import {
    Alert, Button, FlatList, ScrollView, Text, TextInput, TouchableHighlight, TouchableOpacity, useColorScheme, View
} from "react-native";
import {Component, useEffect, useRef, useState} from "react";
import * as React from "react";
import {styles} from "../css";
import {Portrait} from "../component/Portrait";
import {Btn} from "../component/btn";
import {_AddIm, _AddList, _DelIm, _ListNull, _Quser, _User} from "../_Api";
import {useFocusEffect} from "@react-navigation/native";
export function Add({navigation}) {

    const colorScheme = useColorScheme();
    const C1 = colorScheme == 'light' ? styles.lightC1 : styles.darkC1
    const C2 = colorScheme == 'light' ? styles.lightC2 : styles.darkC2
    const MeBbc = colorScheme == 'light' ? styles.lightNsgBcB : styles.darkNsgBcB    //ÂØπËØùÊ°ÜÈ¢úËâ≤
    const MsgColorTouchable = colorScheme == 'light' ? '#EAEAEA' : '#333C52'    //ÁÇπÂáªÂØπËØùÊ°Ü
    const MstText = colorScheme == 'light' ? {color: '#222222'} : {color: '#ffffff'}    //ÂØπËØùÊ°ÜÈ¢úËâ≤
    const MsgColor = colorScheme == 'light' ? styles.lightMsg : styles.darkMsg  //ÂØπËØùÊ°ÜÈÖçËâ≤

    const addRef = useRef(false)

    const [query, setQuery] = useState(false)    //
    const [text, setText] = useState('') //ÊêúÁ¥¢ÁªÜËÖª‰∏ã
    const [queryList, setQueryList] = useState([])  //Êü•ËØ¢ÂàóË°®
    const [user, setUser] = useState({}) //Áî®Êà∑‰ø°ÊÅØ
    const [nullList, setNullList] = useState([])   //ÈùûÂ•ΩÂèãÂàóË°®

    navigation.addListener('focus', () => {
        navigation.setOptions({
            title: "Êñ∞ÁöÑÂØπËØù", headerRight: () => <Btn text={'üë®‚Äçüë©‚Äçüëß‚Äçüë¶'} fs={20} press={() => Alert.alert('ÂàõÂª∫Áæ§ËÅä','Ëøô‰∏™ÂäüËÉΩËøòÂú®ÂºÄÂèë‰∏≠„ÄÅ„ÄÅ',[
                {
                    text:'ok'
                }
            ])}/>
        })
        _User(setUser)  //Ëé∑ÂèñÁî®Êà∑‰ø°ÊÅØ
        _ListNull(cb=>{
            setNullList(cb)
        })    //Ëé∑ÂèñÈùûÂ•ΩÂèã‰ø°ÈÅì

    })


    return <View style={[styles.Add, C1]}>
        {/*ÊêúÁ¥¢ÊúãÂèã*/}
        <View style={[styles.MeInput, {paddingTop: 5}]}>
            <Text style={[MstText, styles.T5, styles.bold]}>ÈÄöËøáid„ÄÅÊòµÁß∞Êü•Êâæ</Text>
            <TextInput value={text} style={[styles.MeInputs,MsgColor, MstText, styles.T5]}
                       onChangeText={text => setText(text)}/>
            <TouchableHighlight underlayColor={MsgColorTouchable} onPress={() => {
                _Quser(setQueryList, text)
            }}>
                <Text style={[MstText, styles.T5, styles.bold, {marginRight: 10}]}>üîç</Text>
            </TouchableHighlight>
        </View>

        {/*ÊêúÁ¥¢Âà∞ÊúãÂèã*/}
        <FlatList
            data={queryList}
            renderItem={({item}) => <View style={styles.ListRow}>
                <Portrait w={38} h={38} r={30} t={'üò¢'} url={item.avatar}/>
                <View style={[styles.ListLi]}>
                    <Text style={[styles.T4, C2, styles.bold]}>{item.name} </Text>
                    <Text style={[styles.T5, C2, styles.bold, {opacity: 0.6}]}>üÜî {item.id}</Text>
                </View>
                <TouchableOpacity onPress={() => {
                    console.log('ÊâìÊãõÂëº', item)
                    _AddList(item._id,cb=>{
                        _ListNull(list=>{
                            setNullList(list)
                        })    //Ëé∑ÂèñÈùûÂ•ΩÂèã‰ø°ÈÅì
                    })
                }}>
                    <Text style={[styles.T2, C2, styles.bold, {marginRight: 20}]}>üëã</Text>
                </TouchableOpacity>
            </View>}
        />

        {/*Á≠âÂæÖÂÆ°Ê†∏ÈÄöËøáÁöÑËÅîÁ≥ª‰∫∫*/}
        <FlatList
            data={nullList}
            renderItem={({item}) => user.id == item.user.id ? <View style={styles.ListRow}>
                <Portrait w={38} h={38} r={30} t={'üò¢'} url={item.userArr[1].avatar}/>
                <View style={[styles.ListLi]}>
                    <Text style={[styles.T4, C2, styles.bold]}>{item.userArr[1].name}</Text>
                    {/*<Text style={[styles.T5, C2, styles.bold, {opacity: 0.6,marginTop: 3,}]}>üÜî {item.userArr[1].id}</Text>*/}
                    <Text style={[styles.T5, C2, styles.bold, {opacity: 0.6,marginTop: 3,}]}>Á≠âÂæÖÂØπÊñπÁ°ÆËÆ§</Text>
                </View>
                <Btn text={'üö´'} fs={20} press={() => Alert.alert("Êí§ÂõûÊ∑ªÂä†Â•ΩÂèãËØ∑Ê±Ç", "", [{
                    text: "ÂèñÊ∂à",onPress: () => console.log("Ask me later pressed")
                }, {
                    text: "Êí§Âõû",
                    style: "destructive",
                    onPress: () => console.log("OK Pressed")}])}/>

            </View> : <View style={styles.ListRow}>
                <Portrait w={38} h={38} r={30} t={'üò¢'} url={item.userArr[0].avatar}/>
                <View style={[styles.ListLi]}>
                    <Text style={[styles.T4, C2, styles.bold]}>{item.userArr[0].name}</Text>
                    <Text style={[styles.T5, C2, styles.bold, {opacity: 0.6,marginTop: 3}]}>üÜî {item.userArr[0].id}</Text>
                </View>
                <Btn text={'‚úÖ'} fs={20} press={() => Alert.alert("ÈÄöËøáÂ•ΩÂèãËØ∑Ê±Ç", "", [{
                    text: "ÂèñÊ∂à", onPress: () => console.log("Ask me later pressed")
                }, {
                    text: "ÈÄöËøá", style:'cancel', onPress: () => _AddIm(item._id,cb=>{
                        _ListNull(setNullList)
                    })
                }])}/>
                <Btn text={'üö´ '} fs={20} press={() => Alert.alert("Âà†Èô§Ê∑ªÂä†Â•ΩÂèãËØ∑Ê±Ç", "", [{
                    text: "ÂèñÊ∂à",onPress: () => console.log("Ask me later pressed")
                }, {
                    text: "Âà†Èô§",
                    style: "destructive",
                    onPress: () => _DelIm(item._id)}])}/>
            </View>}


        />

    </View>
}
